sp_widget.client_script:
api.controller=function() {
  /* widget controller */
	var c = this;
	c.notePos = 0 // note position in teh list to update the record

	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	// Upate list when updating a note.Set the new title and note values on the 
	// c.data object
	$rootScope.$on('updateNote', function(event,data) { 
		console.log('Listener caught for update NoteID: ' + $rootScope.noteID); 
		console.log('Event received: ' + event);
		c.data.notes[c.notePos].title = data.title;
		c.data.notes[c.notePos].note = data.note;

	});

};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.notes = []

	var grNotes = new GlideRecord("x_snc_createnotes_note")
	grNotes.query('user', gs.getUser().getID());
	grNotes.orderByDesc('sys_created_on');
	grNotes.query();

	while(grNotes.next()){
		var noteObj = {};
		//noteObj.number = grNotes.getValue('number');
		//noteObj.title = grNotes.getValue('title');
		//noteObj.sysId = grNotes.getValue('sys_id');
		$sp.getRecordDisplayValues(noteObj, grNotes, 'number,title,sys_id');
		noteObj.note = grNotes.getValue('note').substring(0,20) + '...';
		data.notes.push(noteObj);
	}

})();
